mapper.select:
SELECT 

FROM education_resource a

WHERE a.id = #{id}

SELECT 

FROM education_resource a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.course = #{course}


        AND a.resource_name LIKE 
            '%'||#{resourceName}||'%'
            '%'+#{resourceName}+'%'
            concat('%',#{resourceName},'%')




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT 

FROM education_resource a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE education_resource SET 
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
