SELECT 

FROM preview_report_form a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.report_form_name = #{reportFormNum}

	AND a.course = #{createBy.phone}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM preview_report_form a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
